const countAtom = atom(0);

const Counter = () => {
  const [count, setCount] = useAtom(countAtom);
  const inc = () => setCount((c) => c + 1);
  return (
    <>
      {count} <button onClick={inc}>+1</button>
    </>
  );
};

const App = () => (
  <div className="App">
    <h1>Hello Jotai</h1>
    <h2>Enjoy coding!</h2>
    <Counter />
  </div>
);

export default App;
